import React, { Component } from 'react';

import axios from 'axios'


class Picture extends Component {
 constructor(props){
   super(props);
   this.submitpicture = this.submitpicture.bind(this);
 }

 submitpicture(e){
   
   e.preventDefault();
   const fd = new FormData();
   fd.append('image',e.target.someExpressFiles.files[0],e.target.someExpressFiles.files[0])
   console.log('test',e.target.someExpressFiles.result);
   console.log('e: ',e.target.title.value);
   console.log('file: ',e.target.someExpressFiles.files[0]);
   let image = e.target.someExpressFiles.files[0]
   console.log('image: ',image)
   axios.post('http://localhost:5000/api/upload/',
    fd
).then((response) => {
    console.log(response)
  })

 }
  render() {
  
    return (
      <div >s
        <h2>With <code>"express"</code> npm package</h2>
        <form onSubmit={this.submitpicture} encType="multipart/form-data" method="post">
          <div>Text field title: <input type="text" name="title" /></div>
          <div>File: <input type="file" name="someExpressFiles" multiple="multiple" /></div>
          <input type="submit" value="Upload" />
        </form>
      </div>
    );
  }
  
}


export default Picture;
