import React, { Component } from 'react';

class AddTodo extends Component {
  state = {
    title:'',

  }

  onSubmit = (e) => {
    e.preventDefault();
    console.log(e);

  }
  render() { 
    return ( 
      <div>
        <form onSubmit = {this.onSubmit}>
          <input 
            type="text"
            name="title"
            placeholder = "Add Todo ..."
            value = {this.state.title}
          />
          <input
            type= "submit"
            value = "Submit"
          />
        </form>
      </div>
     );
  }
}
 
export default AddTodo;
